# /// # let err = ChainError::new(String::new(), None, None);
# /// // Instead of writing
# /// err.find_cause::<ChainError<FooErrorKind>>();
# /// // and/or
# /// err.find_chain_cause::<FooErrorKind>();
# /// // and/or
# /// err.find_cause::<FooErrorKind>();
