# # /// map into `ChainError<T>` with `T::from(err)`
# ///
# /// adds `line!()` and `file!()` information
# #[macro_export]
# macro_rules! minto_cherr {
# ( $k:ident ) => (
# |e| $crate::cherr!(e, $k::from(&e))
# );
# ( $enum:ident $(:: $enum_path:ident)* ) => (
