# #[macro_export]
# macro_rules! strerr {
# ( $t:path, $msg:expr ) => ({
# $crate::cherr!($t ($msg.to_string()))
# });
# ( $t:path, $msg:expr, ) => ({
# $crate::cherr!($t ($msg.to_string()))
# });
# ( $t:path, $fmt:expr, $($arg:tt)+ ) => ({
# $crate::cherr!($t (format!($fmt, $($arg)+ )))
# });
# ($msg:expr) => ({
# $crate::cherr!($msg.to_string())
