# //! Ok(())
# //! }
# //!
# //! fn main() {
# //! if let Err(e) = func1() {
# //! #[cfg(not(windows))]
# //! assert_eq!(
# //! format!("\n{:?}\n", e), r#"
# //! src/lib.rs:20: func1 error
# //! Caused by:
# //! src/lib.rs:15: Error reading 'foo.txt'
# //! Caused by:
# //! Kind(NotFound)
# //! "#
# //! );
# //! }
# //! # else {
# //! # unreachable!();
# //! # }
# //! }
# //! ```
