# #[macro_export]
# macro_rules! mstrerr {
# ( $t:path, $msg:expr ) => ({
# |e| $crate::cherr!(e, $t ($msg.to_string()))
# });
# ( $t:path, $msg:expr, ) => ({
# |e| $crate::cherr!(e, $t ($msg.to_string()))
# });
# ( $t:path, $fmt:expr, $($arg:tt)+ ) => ({
# |e| $crate::cherr!(e, $t (format!($fmt, $($arg)+ )))
# });
# ($msg:expr) => ({
# |e| $crate::cherr!(e, $msg.to_string())
# });
# ($msg:expr, ) => ({
# |e| $crate::cherr!(e, $msg.to_string())
# });
# ($fmt:expr, $($arg:tt)+) => ({
# |e| $crate::cherr!(e, format!($fmt, $($arg)+ ))
# });
# }
