# #[macro_export]
# macro_rules! derive_err_kind {
# ($e:ident, $k:ident) => {
# pub struct $e($crate::ChainError<$k>);
# # impl $e {
# pub fn kind(&self) -> &$k {
# self.0.kind()
# }
# }
# # impl From<$k> for $e {
# fn from(e: $k) -> Self {
# $e($crate::ChainError::new(e, None, None))
# }
# }
# # impl From<ChainError<$k>> for $e {
# fn from(e: $crate::ChainError<$k>) -> Self {
# $e(e)
# }
# }
# # impl From<&$e> for $k
# where
# $k: Clone,
# {
# fn from(e: &$e) -> Self {
# e.kind().clone()
# }
# }
# # impl $crate::ChainErrorFrom<$e> for $k
# where
# $k: Clone,
# {
# #[inline]
# fn chain_error_from(
# t: $e,
# line_filename: Option<&'static str>,
# ) -> $crate::ChainError<$k> {
# $crate::ChainError::new((*t.kind()).clone(), Some(Box::from(t)), line_filename)
# }
# }
# # impl std::error::Error for $e {
# fn source(&self) -> Option<&(dyn std::error::Error + 'static)> {
# self.0.source()
# }
# }
# # impl std::fmt::Display for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Display::fmt(&self.0, f)
# }
# }
# # impl std::fmt::Debug for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Debug::fmt(&self.0, f)
# }
# }
# };
# }
# } Note, that because we changed the output of the error in main() from Debug to Display, we don't see the error backtrace with filename and line number. To enable the Display backtrace, you have to enable the feature display-cause for chainerror.

