Because ChainError implements Deref to &T, we can also match on *e instead of e.kind() or call a function with &e use chainerror::prelude::v1::*;
use std::error::Error;
use std::io;
use std::result::Result; fn do_some_io() -> Result<(), Box<dyn Error + Send + Sync>> { Err(io::Error::from(io::ErrorKind::NotFound))?; Ok(())
} derive_str_context!(Func2Error); fn func2() -> Result<(), Box<dyn Error + Send + Sync>> { let filename = "foo.txt"; do_some_io().context(Func2Error(format!("Error reading '{}'", filename)))?; Ok(())
} enum Func1ErrorKind { Func2, IO(String),
} impl ::std::fmt::Display for Func1ErrorKind { fn fmt(&self, f: &mut ::std::fmt::Formatter) -> ::std::fmt::Result { match self { Func1ErrorKind::Func2 => write!(f, "func1 error calling func2"), Func1ErrorKind::IO(filename) => write!(f, "Error reading '{}'", filename), } }
} impl ::std::fmt::Debug for Func1ErrorKind { fn fmt(&self, f: &mut ::std::fmt::Formatter) -> ::std::fmt::Result { write!(f, "{}", self) }
} impl ::std::error::Error for Func1ErrorKind {} fn func1() -> ChainResult<(), Func1ErrorKind> { func2().context(Func1ErrorKind::Func2)?; let filename = String::from("bar.txt"); do_some_io().context(Func1ErrorKind::IO(filename))?; Ok(())
} fn handle_func1errorkind(e: &Func1ErrorKind) { match e { Func1ErrorKind::Func2 => eprintln!("Main Error Report: func1 error calling func2"), Func1ErrorKind::IO(ref filename) => { eprintln!("Main Error Report: func1 error reading '{}'", filename) } }
} fn main() -> Result<(), Box<dyn Error + Send + Sync>> { if let Err(e) = func1() { match *e { Func1ErrorKind::Func2 => eprintln!("Main Error Report: func1 error calling func2"), Func1ErrorKind::IO(ref filename) => { eprintln!("Main Error Report: func1 error reading '{}'", filename) } } handle_func1errorkind(&e); if let Some(e) = e.find_chain_cause::<Func2Error>() { eprintln!("\nError reported by Func2Error: {}", e) } eprintln!("\nDebug Error:\n{:?}", e); std::process::exit(1); } Ok(())
}
# #[allow(dead_code)]
# mod chainerror {
# //! `chainerror` provides an error backtrace without doing a real backtrace, so even after you `strip` your
# //! binaries, you still have the error backtrace.
# //!
# //! Having nested function returning errors, the output doesn't tell where the error originates from.
# //!
# //! ```rust
# //! use std::path::PathBuf;
# //!
# //! type BoxedError = Box<dyn std::error::Error + Send + Sync>;
# //! fn read_config_file(path: PathBuf) -> Result<(), BoxedError> {
# //! // do stuff, return other errors
# //! let _buf = std::fs::read_to_string(&path)?;
# //! // do stuff, return other errors
# //! Ok(())
# //! }
# //!
# //! fn process_config_file() -> Result<(), BoxedError> {
# //! // do stuff, return other errors
# //! let _buf = read_config_file("foo.txt".into())?;
# //! // do stuff, return other errors
# //! Ok(())
# //! }
# //!
# //! fn main() {
# //! if let Err(e) = process_config_file() {
# //! eprintln!("Error:\n{:?}", e);
# //! }
# //! }
# //! ```
# //!
# //! This gives the output:
# //! ```console
# //! Error:
# //! Os { code: 2, kind: NotFound, message: "No such file or directory" }
# //! ```
# //! and you have no idea where it comes from.
# //!
# //!
# //! With `chainerror`, you can supply a context and get a nice error backtrace:
# //!
# //! ```rust
# //! use chainerror::prelude::v1::*;
# //! use std::path::PathBuf;
# //!
# //! type BoxedError = Box<dyn std::error::Error + Send + Sync>;
# //! fn read_config_file(path: PathBuf) -> Result<(), BoxedError> {
# //! // do stuff, return other errors
# //! let _buf = std::fs::read_to_string(&path).context(format!("Reading file: {:?}", &path))?;
# //! // do stuff, return other errors
# //! Ok(())
# //! }
# //!
# //! fn process_config_file() -> Result<(), BoxedError> {
# //! // do stuff, return other errors
# //! let _buf = read_config_file("foo.txt".into()).context("read the config file")?;
# //! // do stuff, return other errors
# //! Ok(())
# //! }
# //!
# //! fn main() {
# //! if let Err(e) = process_config_file() {
# //! eprintln!("Error:\n{:?}", e);
# //! # assert_eq!(
# //! # format!("{:?}\n", e),
# //! # "\
# //! # src/lib.rs:16:51: read the config file\n\
# //! # Caused by:\n\
# //! # src/lib.rs:9:47: Reading file: \"foo.txt\"\n\
# //! # Caused by:\n\
# //! # Os { code: 2, kind: NotFound, message: \"No such file or directory\" }\n\
# //! # ",
# //! # );
# //! }
# //! }
# //! ```
# //!
# //! with the output:
# //! ```console
# //! Error:
# //! examples/simple.rs:14:51: read the config file
# //! Caused by:
# //! examples/simple.rs:7:47: Reading file: "foo.txt"
# //! Caused by:
# //! Os { code: 2, kind: NotFound, message: "No such file or directory" }
# //! ```
# //!
# //! `chainerror` uses `.source()` of `std::error::Error` along with `#[track_caller]` and `Location` to provide a nice debug error backtrace.
# //! It encapsulates all types, which have `Display + Debug` and can store the error cause internally.
# //!
# //! Along with the `ChainError<T>` struct, `chainerror` comes with some useful helper macros to save a lot of typing.
# //!
# //! `chainerror` has no dependencies!
# //!
# //! Debug information is worth it!
# //!
# //! ## Features
# //!
