# /// let filename = "foo.txt";
# /// do_some_io().context(Func2Error(format!("Error reading '{}'", filename)))?;
# /// Ok(())
# /// }
# ///
# /// derive_str_context!(Func1Error);
# ///
# /// fn func1() -> Result<(), Box<dyn Error>> {
# /// func2().context(Func1Error("func1 error".into()))?;
# /// Ok(())
# /// }
# /// # if let Err(e) = func1() {
# /// # if let Some(f1err) = e.downcast_chain_ref::<Func1Error>() {
# /// # assert!(f1err.find_cause::<ChainError<Func2Error>>().is_some());
# /// # assert!(f1err.find_chain_cause::<Func2Error>().is_some());
# /// # } else {
# /// # panic!();
# /// # }
# /// # } else {
# /// # unreachable!();
# /// # }
# /// ```
# #[macro_export]
# macro_rules! derive_str_context {
# ($e:ident) => {
# #[derive(Clone)]
# pub struct $e(pub String);
# impl ::std::fmt::Display for $e {
# fn fmt(&self, f: &mut ::std::fmt::Formatter) -> ::std::fmt::Result {
# write!(f, "{}", self.0)
# }
# }
# impl ::std::fmt::Debug for $e {
# fn fmt(&self, f: &mut ::std::fmt::Formatter) -> ::std::fmt::Result {
# write!(f, "{}({})", stringify!($e), self.0)
# }
# }
# impl ::std::error::Error for $e {}
# };
# }
# # /// Derive an Error for an ErrorKind, which wraps a `ChainError` and implements a `kind()` method
# ///
# /// It basically hides `ChainError` to the outside and only exposes the `kind()`
# /// method.
# ///
# /// Error::kind() returns the ErrorKind
# /// Error::source() returns the parent error
# ///
# /// # Examples
# ///
# /// ```rust
# /// use chainerror::prelude::v1::*;
# /// use std::io;
# ///
# /// fn do_some_io(_f: &str) -> std::result::Result<(), io::Error> {
# /// return Err(io::Error::from(io::ErrorKind::NotFound));
# /// }
# ///
# /// #[derive(Debug, Clone)]
# /// pub enum ErrorKind {
# /// IO(String),
# /// FatalError(String),
# /// Unknown,
# /// }
# ///
# /// derive_err_kind!(Error, ErrorKind);
# ///
# /// impl std::fmt::Display for ErrorKind {
# /// fn fmt(&self, f: &mut ::std::fmt::Formatter) -> std::fmt::Result {
# /// match self {
# /// ErrorKind::FatalError(e) => write!(f, "fatal error {}", e),
# /// ErrorKind::Unknown => write!(f, "unknown error"),
# /// ErrorKind::IO(filename) => write!(f, "Error reading '{}'", filename),
# /// }
# /// }
# /// }
# ///
# /// impl ErrorKind {
# /// fn from_io_error(e: &io::Error, f: String) -> Self {
# /// match e.kind() {
# /// io::ErrorKind::BrokenPipe => panic!("Should not happen"),
# /// io::ErrorKind::ConnectionReset => {
# /// ErrorKind::FatalError(format!("While reading `{}`: {}", f, e))
# /// }
# /// _ => ErrorKind::IO(f),
# /// }
# /// }
# /// }
# ///
# /// impl From<&io::Error> for ErrorKind {
# /// fn from(e: &io::Error) -> Self {
# /// ErrorKind::IO(format!("{}", e))
# /// }
# /// }
# ///
# /// pub fn func1() -> std::result::Result<(), Error> {
# /// let filename = "bar.txt";
# ///
# /// do_some_io(filename).map_context(|e| ErrorKind::from_io_error(e, filename.into()))?;
# /// do_some_io(filename).map_context(|e| ErrorKind::IO(filename.into()))?;
# /// do_some_io(filename).map_context(|e| ErrorKind::from(e))?;
# /// Ok(())
# /// }
# /// ```
# #[macro_export]
# macro_rules! derive_err_kind {
# ($e:ident, $k:ident) => {
# pub struct $e($crate::ChainError<$k>);
# # impl $e {
# pub fn kind(&self) -> &$k {
# self.0.kind()
# }
# }
# # impl From<$k> for $e {
# fn from(e: $k) -> Self {
# $e($crate::ChainError::new(e, None, None))
# }
# }
# # impl From<ChainError<$k>> for $e {
# fn from(e: $crate::ChainError<$k>) -> Self {
# $e(e)
# }
# }
# # impl From<&$e> for $k
# where
# $k: Clone,
# {
# fn from(e: &$e) -> Self {
# e.kind().clone()
# }
# }
# # impl std::error::Error for $e {
# fn source(&self) -> Option<&(dyn std::error::Error + 'static)> {
# self.0.source()
# }
# }
# # impl std::fmt::Display for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Display::fmt(&self.0, f)
# }
# }
# # impl std::fmt::Debug for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Debug::fmt(&self.0, f)
# }
# }
# };
# }
# }
pub mod mycrate { use crate::chainerror::*; // omit the `crate::` part use std::io; fn do_some_io() -> std::result::Result<(), Box<dyn std::error::Error + Send + Sync>> { Err(io::Error::from(io::ErrorKind::NotFound))?; Ok(()) } derive_str_context!(Func2Error); fn func2() -> std::result::Result<(), Box<dyn std::error::Error + Send + Sync>> { let filename = "foo.txt"; do_some_io().context(Func2Error(format!("Error reading '{}'", filename)))?; Ok(()) } #[derive(Debug, Clone)] pub enum ErrorKind { Func2, IO(String), } derive_err_kind!(Error, ErrorKind); pub type Result<T> = std::result::Result<T, Error>; impl std::fmt::Display for ErrorKind { fn fmt(&self, f: &mut ::std::fmt::Formatter) -> std::fmt::Result { match self { ErrorKind::Func2 => write!(f, "func1 error calling func2"), ErrorKind::IO(filename) => write!(f, "Error reading '{}'", filename), } } } pub fn func1() -> Result<()> { func2().context(ErrorKind::Func2)?; let filename = String::from("bar.txt"); do_some_io().context(ErrorKind::IO(filename))?; Ok(()) }
} fn main() -> Result<(), Box<dyn std::error::Error + Send + Sync>> { use mycrate::func1; use mycrate::ErrorKind; use std::error::Error; use std::io; if let Err(e) = func1() { match e.kind() { ErrorKind::Func2 => eprintln!("Main Error Report: func1 error calling func2"), ErrorKind::IO(ref filename) => { eprintln!("Main Error Report: func1 error reading '{}'", filename) } } eprintln!(); let mut s: &dyn Error = &e; while let Some(c) = s.source() { if let Some(ioerror) = c.downcast_ref::<io::Error>() { eprintln!("caused by: std::io::Error: {}", ioerror); match ioerror.kind() { io::ErrorKind::NotFound => eprintln!("of kind: std::io::ErrorKind::NotFound"), _ => {} } } else { eprintln!("caused by: {}", c); } s = c; } eprintln!("\nDebug Error:\n{:?}", e); std::process::exit(1); } Ok(())
}

