# /// use std::io;
# ///
# /// fn do_some_io(_f: &str) -> std::result::Result<(), io::Error> {
# /// return Err(io::Error::from(io::ErrorKind::NotFound));
# /// }
# ///
# /// #[derive(Debug, Clone)]
# /// pub enum ErrorKind {
# /// IO(String),
# /// FatalError(String),
# /// Unknown,
# /// }
# ///
# /// derive_err_kind!(Error, ErrorKind);
