# /// do_some_io(filename).map_context(|e| ErrorKind::IO(filename.into()))?;
# /// do_some_io(filename).map_context(|e| ErrorKind::from(e))?;
# /// Ok(())
# /// }
# /// ```
# #[macro_export]
# macro_rules! derive_err_kind {
# ($e:ident, $k:ident) => {
# pub struct $e($crate::ChainError<$k>);
# # impl $e {
# pub fn kind(&self) -> &$k {
# self.0.kind()
# }
# }
# # impl From<$k> for $e {
# fn from(e: $k) -> Self {
# $e($crate::ChainError::new(e, None, None))
# }
# }
# # impl From<ChainError<$k>> for $e {
# fn from(e: $crate::ChainError<$k>) -> Self {
# $e(e)
# }
# }
# # impl From<&$e> for $k
# where
# $k: Clone,
# {
# fn from(e: &$e) -> Self {
# e.kind().clone()
# }
# }
# # impl std::error::Error for $e {
# fn source(&self) -> Option<&(dyn std::error::Error + 'static)> {
# self.0.source()
# }
# }
# # impl std::fmt::Display for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Display::fmt(&self.0, f)
# }
# }
# # impl std::fmt::Debug for $e {
# fn fmt(&self, f: &mut std::fmt::Formatter) -> std::fmt::Result {
# std::fmt::Debug::fmt(&self.0, f)
# }
# }
# };
# }
# } If you compare the output to the previous example, you will see, that: Error: src/main.rs:19: "func1 error" changed to just: src/main.rs:16: "func1 error" This is, because we caught the error of func1() in main() and print it out ourselves. We can now control, whether to output in Debug or Display mode. Maybe depending on --debug as a CLI argument.

