# pub fn new(
# kind: T,
# error_cause: Option<Box<dyn Error + 'static>>,
# _occurrence: Option<(u32, &'static str)>,
# ) -> Self {
# Self { kind, error_cause }
# }
# # /// return the root cause of the error chain, if any exists
