# #[macro_export]
# macro_rules! cherr {
# ( $k:expr ) => {
# ChainError::<_>::new($k, None, Some((line!(), file!())))
# };
# ( $e:expr, $k:expr ) => {
# ChainError::<_>::new($k, Some(Box::from($e)), Some((line!(), file!())))
# };
